    return \'\';
} elseif (empty($status[\'total_count\'])) {
    return $pdoFetch->getChunk($tpl);
}

// Select cart products
$where = array(
    \'msProduct.id:IN\' => array(),
);
foreach ($cart as $entry) {
    $where[\'msProduct.id:IN\'][] = $entry[\'id\'];
}
