    \'class\' => \'msOrderProduct\',
    \'where\' => $where,
    \'leftJoin\' => $leftJoin,
    \'select\' => $select,
    \'joinTVsTo\' => \'msProduct\',
    \'sortby\' => \'msOrderProduct.id\',
    \'sortdir\' => \'asc\',
    \'groupby\' => \'msOrderProduct.id\',
    \'fastMode\' => false,
    \'limit\' => 0,
