    \'delivery_cost\' => $miniShop2->formatPrice($order->get(\'delivery_cost\')),
    \'weight\' => $miniShop2->formatWeight($order->get(\'weight\')),
    \'cart_weight\' => $miniShop2->formatWeight($order->get(\'weight\')),
    \'cart_count\' => $cart_count,
);

